// Shader File
declare module "*.vert" {
  const src: string;
  export default src;
}

declare module "*.frag" {
  const src: string;
  export default src;
}

// Image File
declare module "*.png" {
  const value: string;
  export default value;
}

declare module "*.svg" {
  const value: string;
  export default value;
}

declare module "*.jpg" {
  const value: string;
  export default value;
}

declare module "*.gif" {
  const value: string;
  export default value;
}

// 3D Model Data
declare module "*.obj" {
  const value: string;
  export default value;
}

// Sound and Movie Data
declare module "*.mp3" {
  const value: string;
  export default value;
}

declare module "*.ogg" {
  const value: string;
  export default value;
}

declare module "*.mp4" {
  const value: string;
  export default value;
}
